    private void btnDescifrarTextoAtbashMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_btnDescifrarTextoAtbashMouseClicked
        Atbash at = new Atbash();
        String texto = txtTextoADescifrarAtbash.getText();

        String resultado = at.descifrarTextoAtbash(texto);
        txtResultadoTextoDescifradoAtbash.setText(resultado);

    }//GEN-LAST:event_btnDescifrarTextoAtbashMouseClicked

