    private void txtTextoADescifrarAtbashKeyTyped(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_txtTextoADescifrarAtbashKeyTyped
        int key = evt.getKeyChar();

        boolean mayusculas = key >= 65 && key <= 90;
        boolean minusculas = key >= 97 && key <= 122;
        boolean espacio = key == 32;

        if(!(mayusculas || minusculas || espacio)){
            evt.consume();
        }
    }//GEN-LAST:event_txtTextoADescifrarAtbashKeyTyped

