    private void btnCifrarTextoOTPMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_btnCifrarTextoOTPMouseClicked
        OneTimePad otp = new OneTimePad();
        String texto = txtTextoACifrarOTP.getText();
        String clave = txtClaveDeCifradoOTP.getText();

        String resultado = otp.cifrarTextoOneTimePad(texto, clave);
        txtResultadoTextoCifradoOTP.setText(resultado);

    }//GEN-LAST:event_btnCifrarTextoOTPMouseClicked

